Answer1.56:
Simplest variant of Ethereum smart-contract bellow.
The process goes something like this:

"Main node" for each task creates a smart contract "Task" specifying the name of the task, its description (for example, URL or IPFS-manifest file with a description) and a list of viewers accounts.

The viewer on its node catches the TaskOpened event and analyzes the task

If the Viewer confirms the completion of the task, it transactionally calls method AcceptTask, if it prohibits it, it calls method RejectTask

If all Viewers confirm the completion of the task, then at the moment of the last confirmation event TaskConfirmed is generated, which catches the "Master node" and any viewers

If at least one of the Viewers prohibits the task execution, event TaskDeclined is generated immediately, which catches the "Master node" and any viewers
pragma solidity >=0.5.2 <0.6.0;
contract Task
{
address    Owner ;
 bytes32    Name ;
 string     Description ;
 bytes32    State ;

 uint256     AnswersWaitCnt ;

 struct Viewer
 {
   bytes32  reply ;
   string   remark ;
 }

 mapping (address => Viewer) ViewersState ;

 event TaskAssigned (address viewer, bytes32 name, string description) ;
 event TaskConfirmed(bytes32 name) ; 
 event TaskDeclined (bytes32 name, address viewers, string remark) ; 

 constructor(bytes32 name_, string memory description_, address[] memory viewers_) public

 { 
     uint256  i    ;

           Owner= tx.origin ;

            Name= name_ ;
     Description= description_  ;
  AnswersWaitCnt= viewers_.length ;

           State="New"  ;


       for(i=0 ; i<viewers_.length ; i++) 
       {
           ViewersState[ viewers_[i] ]=Viewer({  reply: "wait", 
                                                remark: ""      }) ;

                emit TaskAssigned(viewers_[i], Name, Description) ; 
       }
 }

 function GetState() public view returns (bytes32, bytes32, string memory retVal)
 {
    return(Name, State, Description) ;
 }

 function AcceptTask() public
 {
     if( ViewersState[ tx.origin ].reply== 0x00 )  return ;
     if( ViewersState[ tx.origin ].reply!="wait")  return ;

         ViewersState[ tx.origin ].reply="accept" ;

            AnswersWaitCnt-- ;

     if(AnswersWaitCnt==0)
     {
           State="Confirmed"  ;
       emit TaskConfirmed(Name) ;
     }
 }

 function RejectTask(string memory remark_) public
 {
     if( ViewersState[ tx.origin ].reply== 0x00 )  return ;
     if( ViewersState[ tx.origin ].reply!="wait")  return ;

         ViewersState[ tx.origin ].reply ="deny" ;
         ViewersState[ tx.origin ].remark= remark_ ;

           State="Declined" ;

       emit TaskDeclined(Name, tx.origin, remark_) ;
 }

}

