  useEffect(() => {
    if (window.ethereum) {
      <web3>.current = new <web3>(window.ethereum)
    }
  }, [])

  /* â Passed through context as <web3> instance */
  const [<web3>, set<web3>] = useState(null)
  useEffect(() => {
    if (window.ethereum) {
      const w3 = new <web3>(window.ethereum)
